import React, { Component } from "react";
import PostBox from "../../Components/PostBox";
import './PostingScreenStyle.css';

export default class PostingScreen extends Component{

     constructor(props){
        super(props);

        this.state = {
            posts: null
        }

        this.getPosts = this.getPosts.bind(this);
    }

    componentDidMount(){
        this.getPosts();
    }

    componentWillUnmount(){

    }

    render(){
        return(
            <div id="pageBox">
                <ul>
                    {
                        !this.state.posts ? <>loading</> : (
                            this.state.posts.map((val) => {
                                // <PostBox title={val.title} description={val.description}></PostBox>
                                <h1>{val.title}</h1>
                            })

                        )
                    }   
                </ul>
            </div>

            // <div>
            //     <h1>{this.state.count}</h1>
            //     <button onClick={this.delta}>+</button>
            // </div>
        );
    }

    async getPosts(){
        let data = await fetch('http://localhost:5001/posts')
            .then(function(response){
                return response.json();
            }).then(function(data){
                console.log(data);
                return data
            });

            this.setState({
                posts: data
            });
    }
}
