import React, { Component } from "react";
import PostBox from "../../Components/PostBox";
import './PostingScreenStyle.css';

export default class PostingScreen extends Component{

     constructor(props){
        super(props);

        this.state = {
            loading: true,
            posts: null,
            titleInput: '',
            descInput: ''
        }

        this.getPosts = this.getPosts.bind(this);
        this.handleDescriptionInputChange = this.handleDescriptionInputChange.bind(this);
        this.handleTitleInputChange = this.handleTitleInputChange.bind(this);
    }

    componentDidMount(){
        this.getPosts();
    }

    componentWillUnmount(){

    }

    render(){
        return(
            <div id="pageBox">
                <div>

                    <form id="newPostInputForm" onSubmit={this.submitNewPost}>
                        <input placeholder="Title" onChange={this.handleTitleInputChange}>

                        </input>
                        <input placeholder="Description" onChange={this.handleDescriptionInputChange}>

                        </input>
                        <button type="submit">submit</button>
                    </form>

                    {
                        !this.state.posts && this.state.loading ? (<>loading</>) : (
                            this.state.posts.map((val, key) => {
                                return (
                                    <PostBox title={val.title} description={val.description} key={key}></PostBox>
                                );
                            })
                        )
                    }
                </div>
            </div>
        );
    }

    async getPosts(){
        this.setState({
            loading: true
        })
        fetch('http://localhost:5001/posts').then(
            (res) =>{
                return res.json()
            }).then(
                (data)=>{
                    console.log(data);
                    this.setState({
                        posts: data,
                        loading: false
                    });
                }
            );
            // this.setState({
            //     posts: data
            // });
    }

    handleTitleInputChange(event){
        this.setState({titleInput: event.target.value}).then(console.log(this.state.titleInput));
    }

    handleDescriptionInputChange(event){
        this.setState({descInput: event.target.value});
    }

    submitNewPost(e){
        e.preventDefault();
        console.log("you submitted");
    }
}
