import React, { Component } from "react";
import PostBox from "../../Components/PostBox";
import './PostingScreenStyle.css';

export default class PostingScreen extends Component{

     constructor(props){
        super(props);

        this.state = {
            posts: null
        }

        this.getPosts = this.getPosts.bind(this);
    }

    componentDidMount(){
        this.getPosts();
    }

    componentWillUnmount(){

    }

    render(){
        return(
            <div id="pageBox">
                <div>

                {!this.state.posts ? (<div>loading...</div>
                    ) : (
                    this.state.posts.map((val, key) => {
                        return (
                            <PostBox title={val.title} description={val.description} key={key} />
                        );
                    })
                )}

                    {/* {
                        !this.state.posts ? <>loading</> : (
                            this.state.posts.map((val, key) => {
                                return (
                                    <PostBox title={val["title"]} description={val["description"]}></PostBox>
                                );
                            })
                        )
                    } */}
                </div>
            </div>
        );
    }

    async getPosts(){
        // let data = await fetch('http://localhost:5001/posts')
        //     .then(function(response){
        //         return response.json();
        //     }).then(function(data){
        //         console.log(data);
        //         return data
        //     });


        fetch('http://localhost:5001/posts').then(
            (res) =>{
                return res.json()
            }).then(
                (data)=>{
                    console.log(data);
                    this.setState({
                        posts: data
                    });
                }
            );
            // this.setState({
            //     posts: data
            // });
    }
}
